changeSet.sql:
CREATE TABLE collection_descriptor( key serial NOT NULL PRIMARY KEY, title text NOT NULL CHECK (assert_min_length(title, 1)), description text CHECK (assert_min_length(description, 1)), collection_key uuid NOT NULL REFERENCES collection(key) ON DELETE CASCADE, created timestamptz NOT NULL DEFAULT now(), created_by varchar NOT NULL CHECK (assert_min_length(created_by, 3)), modified timestamptz NOT NULL DEFAULT now(), modified_by varchar NOT NULL CHECK (assert_min_length(modified_by, 3)), deleted timestamptz NULL, fulltext_search tsvector );
CREATE INDEX collection_descriptor_fulltext_search_idx ON collection_descriptor USING gin(fulltext_search);
CREATE TABLE collection_descriptor_record( key serial NOT NULL PRIMARY KEY, collection_descriptor_key integer NOT NULL REFERENCES collection_descriptor(key) ON DELETE CASCADE, scientific_name text, country text, individual_count integer, identified_by text[], date_identified text, type_status text[], recorded_by text[], discipline text, object_classification_name text, issues text[], fulltext_search tsvector );
-- TODO: index for the fields that we search for?
CREATE INDEX collection_descriptor_record_fulltext_search_idx ON collection_descriptor_record USING gin(fulltext_search);
CREATE TABLE collection_descriptor_verbatim_field( key serial NOT NULL PRIMARY KEY, collection_descriptor_record_key integer NOT NULL REFERENCES collection_descriptor_record(key) ON DELETE CASCADE, field_name text, field_value text, fulltext_search tsvector );
CREATE INDEX collection_descriptor_verbatim_field_fulltext_search_idx ON collection_descriptor_verbatim_field USING gin(fulltext_search);
CREATE OR REPLACE FUNCTION collection_descriptor_change_trigger() RETURNS TRIGGER AS $change$ BEGIN NEW.fulltext_search := TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.title,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.description,''))); RETURN NEW; END; $change$ LANGUAGE plpgsql;
CREATE TRIGGER collection_descriptor_fulltext_update BEFORE INSERT OR UPDATE ON collection_descriptor FOR EACH ROW EXECUTE PROCEDURE collection_descriptor_change_trigger();
CREATE OR REPLACE FUNCTION collection_descriptor_record_change_trigger() RETURNS TRIGGER AS $recordchange$ BEGIN NEW.fulltext_search := TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.scientific_name,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.country,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.identified_by, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.date_identified,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.type_status, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.recorded_by, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.discipline,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.object_classification_name,''))); RETURN NEW; END; $recordchange$ LANGUAGE plpgsql;
CREATE TRIGGER collection_descriptor_record_fulltext_update BEFORE INSERT OR UPDATE ON collection_descriptor_record FOR EACH ROW EXECUTE PROCEDURE collection_descriptor_record_change_trigger();
CREATE OR REPLACE FUNCTION collection_descriptor_verbatim_field_change_trigger() RETURNS TRIGGER AS $fieldchange$ BEGIN NEW.fulltext_search := TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.field_value,''))); RETURN NEW; END; $fieldchange$ LANGUAGE plpgsql;
CREATE TRIGGER collection_descriptor_verbatim_field_fulltext_update BEFORE INSERT OR UPDATE ON collection_descriptor_verbatim_field FOR EACH ROW EXECUTE PROCEDURE collection_descriptor_verbatim_field_change_trigger();
