changeSet.sql:
-- Add biome column to collection_descriptor table ALTER TABLE collection_descriptor ADD COLUMN biome text;
-- Update the fulltext search trigger to include biome field CREATE OR REPLACE FUNCTION collection_descriptor_change_trigger() RETURNS TRIGGER AS $recordchange$ BEGIN NEW.fulltext_search := TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.usage_name,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.usage_rank,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.country,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.identified_by, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.type_status, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(array_to_string(NEW.recorded_by, ' '),''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.discipline,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.object_classification_name,''))) || TO_TSVECTOR('pg_catalog.english', unaccent(COALESCE(NEW.biome,''))); RETURN NEW; END; $recordchange$ LANGUAGE plpgsql;
-- Add index for biome field for better search performance CREATE INDEX collection_descriptor_biome_idx ON collection_descriptor(biome);
