mapper.select:
SELECT 
FROM organization o
WHERE o.key = #{key,jdbcType=OTHER}

SELECT title
FROM organization
WHERE key = #{key,jdbcType=OTHER}

SELECT FROM organization o
JOIN to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{query}))),'\s+',':*&','g')||':*') AS query ON
WHERE deleted IS NULL
AND o.country = #{country,jdbcType=OTHER}
AND o.endorsement_approved = #{isEndorsed,jdbcType=BOOLEAN}
AND EXISTS ( SELECT d2.publishing_organization_key FROM dataset d2 JOIN dataset_network nk ON nk.dataset_key = d2.key JOIN network n ON n.key = nk.network_key AND n.deleted IS NULL WHERE d2.publishing_organization_key = o.key AND n.key = #{networkKey,jdbcType=OTHER})
ORDER BY ts_rank_cd(o.fulltext_search, query) DESC, o.created DESC, key
LIMIT #{page.limit} OFFSET #{page.offset}

SELECT COUNT(*) FROM organization o
JOIN to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{query}))),'\s+',':*&','g')||':*') AS query ON
WHERE deleted IS NULL
AND o.country = #{country,jdbcType=OTHER}
AND o.endorsement_approved = #{isEndorsed,jdbcType=BOOLEAN}
AND EXISTS ( SELECT d2.publishing_organization_key FROM dataset d2 JOIN dataset_network nk ON nk.dataset_key = d2.key JOIN network n ON n.key = nk.network_key AND n.deleted IS NULL WHERE d2.publishing_organization_key = o.key AND n.key = #{networkKey,jdbcType=OTHER})

mapper.update:
UPDATE organization_contact
SET is_primary=false
WHERE organization_key=#{targetEntityKey,jdbcType=OTHER} AND type=#{type,jdbcType=OTHER}

UPDATE organization_contact
SET is_primary=#{primary,jdbcType=BOOLEAN},
    type=#{type,jdbcType=OTHER}
WHERE organization_key=#{targetEntityKey,jdbcType=OTHER} AND contact_key=#{contactKey,jdbcType=INTEGER}

mapper.select:
SELECT true
FROM organization_contact
WHERE organization_key = #{targetEntityKey,jdbcType=OTHER}
  AND contact_key = #{contactKey,jdbcType=INTEGER}
LIMIT 1

mapper.delete:
DELETE FROM machine_tag USING organization_machine_tag
WHERE
machine_tag_key = machine_tag.key AND
organization_key = #{targetEntityKey,jdbcType=OTHER} AND
namespace = #{namespace}

  AND name = #{name}

mapper.select:
SELECT DISTINCT ON (o.key) 
FROM organization o
INNER JOIN organization_machine_tag om ON om.organization_key = o.key
INNER JOIN machine_tag m ON om.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}


ORDER BY o.key, o.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT o.key)
FROM organization o
INNER JOIN organization_machine_tag om ON om.organization_key = o.key
INNER JOIN machine_tag m ON om.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}

SELECT 
FROM organization o
WHERE o.deleted IS NULL AND o.endorsement_approved = false

  AND o.endorsing_node_key= #{nodeKey,jdbcType=OTHER}

ORDER BY o.created DESC, o.key

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT 
FROM organization o
WHERE o.deleted IS NULL AND o.country = #{country,jdbcType=OTHER}
ORDER BY o.created DESC, o.key

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(*)
FROM organization o
WHERE o.deleted IS NULL AND o.country = #{country,jdbcType=OTHER}

SELECT DISTINCT ON (o.key) 
FROM organization o
INNER JOIN organization_identifier ON organization_key = o.key
INNER JOIN identifier ide ON identifier_key = ide.key
WHERE identifier = #{identifier,jdbcType=VARCHAR}

  AND ide.type=#{type,jdbcType=OTHER}

ORDER BY o.key, o.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT o.key)
FROM organization o
INNER JOIN organization_identifier ON organization_key = o.key
INNER JOIN identifier ide ON identifier_key = ide.key
WHERE identifier = #{identifier,jdbcType=VARCHAR}

  AND ide.type=#{type,jdbcType=OTHER}

mapper.update:
UPDATE organization
SET challenge_code_key = #{challengeCodeKey}
WHERE key = #{key}
