mapper.update:
UPDATE public.user
SET challenge_code_key = #{challengeCodeKey}
WHERE key = #{key}

UPDATE public.user
SET last_login = now()
WHERE key = #{key}

mapper.select:
SELECT key
FROM editor_rights
WHERE username = #{userName,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM editor_rights
WHERE username = #{userName,jdbcType=VARCHAR}
  AND key = #{key,jdbcType=OTHER}

DELETE FROM namespace_rights
WHERE username = #{userName,jdbcType=VARCHAR}
  AND namespace = #{namespace,jdbcType=VARCHAR}

mapper.select:
SELECT namespace
FROM namespace_rights
WHERE username = #{userName,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM country_rights
WHERE username = #{userName,jdbcType=VARCHAR}
AND country = #{country,jdbcType=VARCHAR}

mapper.select:
SELECT country
FROM country_rights
WHERE username = #{userName,jdbcType=VARCHAR}
