mapper.sql:
FROM collection c
LEFT JOIN institution inst ON inst.key = c.institution_key




  INNER JOIN collection_machine_tag cmt on cmt.collection_key = c.key
  INNER JOIN machine_tag mt on mt.key = cmt.machine_tag_key


  INNER JOIN collection_identifier ci on ci.collection_key = c.key
  INNER JOIN identifier id on id.key = ci.identifier_key


  LEFT JOIN address addr ON addr.key = c.address_key
  LEFT JOIN address mail_addr ON mail_addr.key = c.mailing_address_key




      c.deleted IS NOT NULL


      c.deleted IS NULL



    AND c.institution_key = #{params.institutionKey,jdbcType=OTHER}


    AND lower(c.code) = lower(#{params.code,jdbcType=VARCHAR})


    AND c.alternative_codes ?? #{params.alternativeCode,jdbcType=VARCHAR}


    AND c.name = #{params.name,jdbcType=VARCHAR}


    AND mt.namespace = #{params.machineTagNamespace,jdbcType=VARCHAR}


    AND mt.name = #{params.machineTagName,jdbcType=VARCHAR}


    AND mt.value = #{params.machineTagValue,jdbcType=VARCHAR}


    AND id.type = #{params.identifierType,jdbcType=OTHER}


    AND id.identifier = #{params.identifier,jdbcType=VARCHAR}


    AND (addr.country IN

        #{item,jdbcType=VARCHAR}

      OR mail_addr.country IN

        #{item,jdbcType=VARCHAR}

    )


    AND (addr.country IN

      #{item,jdbcType=VARCHAR}

    OR mail_addr.country IN

      #{item,jdbcType=VARCHAR}

    )


    AND (normalize_name(addr.city) = normalize_name(#{params.city,jdbcType=VARCHAR})
    OR
    normalize_name(mail_addr.city) = normalize_name(#{params.city,jdbcType=VARCHAR}))


    AND similar_name(c.name, #{params.fuzzyName,jdbcType=VARCHAR})


    AND c.content_type @> ARRAY

      #{item,jdbcType=VARCHAR}



    AND c.preservation_type @> ARRAY

      #{item,jdbcType=VARCHAR}



    AND c.accession_status = #{params.accessionStatus,jdbcType=OTHER}


    AND c.active = #{params.active,jdbcType=BOOLEAN}


    AND c.personal_collection = #{params.personalCollection,jdbcType=BOOLEAN}


    AND c.master_source = #{params.masterSourceType,jdbcType=OTHER}




        AND c.number_specimens = #{params.numberSpecimens.exactValue,jdbcType=INTEGER}



          AND c.number_specimens >= #{params.numberSpecimens.lowerBound,jdbcType=INTEGER}


          AND c.number_specimens <= #{params.numberSpecimens.higherBound,jdbcType=INTEGER}







        AND c.occurrence_count = #{params.occurrenceCount.exactValue,jdbcType=INTEGER}



          AND c.occurrence_count >= #{params.occurrenceCount.lowerBound,jdbcType=INTEGER}


          AND c.occurrence_count <= #{params.occurrenceCount.higherBound,jdbcType=INTEGER}







        AND c.type_specimen_count = #{params.typeSpecimenCount.exactValue,jdbcType=INTEGER}



          AND c.type_specimen_count >= #{params.typeSpecimenCount.lowerBound,jdbcType=INTEGER}


          AND c.type_specimen_count <= #{params.typeSpecimenCount.higherBound,jdbcType=INTEGER}





    AND c.display_on_NHCPortal = #{params.displayOnNHCPortal,jdbcType=BOOLEAN}


    AND c.replaced_by = #{params.replacedBy,jdbcType=OTHER}

mapper.select:
SELECT , inst.name institutionName, inst.code institutionCode FROM collection c LEFT JOIN institution inst ON inst.key = c.institution_key WHERE c.deleted IS NOT NULL
AND c.replaced_by = #{replacedBy,jdbcType=OTHER}
ORDER BY c.created DESC, c.key
LIMIT #{page.limit} OFFSET #{page.offset}

mapper.update:
UPDATE collection SET occurrence_count = aux.occ_count, type_specimen_count = aux.ts_count FROM ( VALUES
(#{item.key,jdbcType=OTHER}, #{item.occurrenceCount,jdbcType=INTEGER}, #{item.typeSpecimenCount,jdbcType=INTEGER})
) as aux (entity_key, occ_count, ts_count) WHERE key = aux.entity_key::uuid

mapper.delete:
DELETE FROM machine_tag USING collection_machine_tag
WHERE
machine_tag_key = machine_tag.key AND
collection_key = #{targetEntityKey,jdbcType=OTHER} AND
namespace = #{namespace}

  AND name = #{name}

mapper.select:
SELECT DISTINCT ON (c.key) 
FROM collection c
INNER JOIN collection_machine_tag cm ON cm.collection_key = c.key
INNER JOIN machine_tag m ON cm.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}


ORDER BY c.key, c.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT c.key)
FROM collection c
INNER JOIN collection_machine_tag cm ON cm.collection_key = c.key
INNER JOIN machine_tag m ON cm.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}

mapper.update:
DELETE FROM occurrence_mapping USING collection_occurrence_mapping
WHERE
occurrence_mapping_key = occurrence_mapping.key
AND collection_key = #{targetEntityKey,jdbcType=OTHER}
AND occurrence_mapping_key = #{occurrenceMappingKey,jdbcType=INTEGER}

mapper.insert:
UPDATE collection
SET master_sync_metadata_key = #{metadataKey,jdbcType=INTEGER},
    master_source = #{masterSourceType,jdbcType=OTHER}
WHERE key = #{targetEntityKey,jdbcType=OTHER}

