SELECT matches.score, matches.type, matches.key, matches.code, matches.name, matches.institution_key, matches.institution_code, matches.institution_name, matches.display_on_NHCPortal, ad.country country, mail.country mail_country
,ts_headline(matches.code, query) code_highlight, ts_headline(matches.name, query) name_highlight ,ts_headline(matches.description, query) description_highlight ,ts_headline(array_to_string(akeys(matches.alternative_codes), ' '), query) alternative_codes_highlight ,ts_headline(ad.address, query) address_highlight,ts_headline(ad.city, query) city_highlight ,ts_headline(ad.province, query) province_highlight,ts_headline(ad.country, query) country_highlight ,ts_headline(mail.address, query) mail_address_highlight,ts_headline(mail.city, query) mail_city_highlight ,ts_headline(mail.province, query) mail_province_highlight, ts_headline(mail.country, query) mail_country_highlight ,matches.similarity_match
FROM (
( SELECT DISTINCT ON (ts_rank_cd(i.fulltext_search, query), i.created, i.key) i.key, i.code, i.name, i.description, i.alternative_codes, i.display_on_NHCPortal, 'institution' AS type, ts_rank_cd(i.fulltext_search, query) score, query, i.address_key, i.mailing_address_key, null::uuid institution_key, null institution_name, null institution_code, false AS similarity_match FROM institution i
INNER JOIN to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') AS query ON query @@ fulltext_search
WHERE i.deleted IS NULL
AND i.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
UNION ALL ( SELECT DISTINCT ON (i.key) i.key, i.code, i.name, i.description, i.alternative_codes, i.display_on_NHCPortal, 'institution' AS type, 9 AS score, to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') query, i.address_key, i.mailing_address_key, null::uuid institution_key, null institution_name, null institution_code, false AS similarity_match FROM institution i WHERE i.deleted IS NULL AND i.code = trim(#{q})
AND i.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
UNION ALL ( SELECT DISTINCT ON (i.key) i.key, i.code, i.name, i.description, i.alternative_codes, i.display_on_NHCPortal AS displayOnNHCPortal, 'institution' AS typeâ‰ˆ, similarity(i.name, #{q}) AS score, to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') query, i.address_key, i.mailing_address_key, null::uuid institution_key, null institution_name, null institution_code, true AS similarity_match FROM institution i WHERE i.deleted IS NULL AND similarity(i.name, #{q}) >= 0.65
AND i.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
UNION ALL ( SELECT DISTINCT ON (ts_rank_cd(c.fulltext_search, query), c.created, c.key) c.key, c.code, c.name, c.description, c.alternative_codes, c.display_on_NHCPortal, 'collection' AS type, ts_rank_cd(c.fulltext_search, query) score, query, c.address_key, c.mailing_address_key, i.key institution_key, i.name institution_name, i.code institution_code, false AS similarity_match FROM collection c
INNER JOIN to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') AS query ON query @@ fulltext_search
LEFT JOIN institution i ON i.key = c.institution_key WHERE c.deleted IS NULL
AND c.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
UNION ALL ( SELECT DISTINCT ON (c.key) c.key, c.code, c.name, c.description, c.alternative_codes, c.display_on_NHCPortal, 'collection' AS type, 9 AS score, to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') query, c.address_key, c.mailing_address_key, i.key institution_key, i.name institution_name, i.code institution_code, false AS similarity_match FROM collection c LEFT JOIN institution i ON i.key = c.institution_key WHERE c.deleted IS NULL AND c.code = trim(#{q})
AND c.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
UNION ALL ( SELECT DISTINCT ON (c.key) c.key, c.code, c.name, c.description, c.alternative_codes, c.display_on_NHCPortal, 'collection' AS type, similarity(c.name, #{q}) AS score, to_tsquery('english', regexp_replace(quote_literal(unaccent(trim(#{q}))),'\s+',':*&','g')||':*') query, c.address_key, c.mailing_address_key, i.key institution_key, i.name institution_name, i.code institution_code, true AS similarity_match FROM collection c LEFT JOIN institution i ON i.key = c.institution_key WHERE c.deleted IS NULL AND similarity(c.name, #{q}) >= 0.65
AND c.display_on_NHCPortal = #{displayOnNHCPortal,jdbcType=BOOLEAN}
)
) AS matches LEFT JOIN address ad ON ad.key = matches.address_key LEFT JOIN address mail ON mail.key = matches.mailing_address_key
ad.country = #{country,jdbcType=OTHER} OR mail.country = #{country,jdbcType=OTHER}
ORDER BY matches.score desc
LIMIT #{limit}
