mapper.select:
SELECT 
FROM institution i
WHERE i.key = #{key,jdbcType=OTHER}

SELECT count(1)
FROM institution i
WHERE i.key = #{key,jdbcType=OTHER}

SELECT 
FROM address
WHERE key = #{key,jdbcType=OTHER}

SELECT DISTINCT ON (ts_rank_cd(i.fulltext_search, query), similarity_score, i.key) i.key, i.name, i.latitude, i.longitude ,similarity(i.name, #{params.fuzzyName,jdbcType=VARCHAR}) AS similarity_score
AND i.latitude IS NOT NULL AND i.longitude IS NOT NULL ORDER BY ts_rank_cd(i.fulltext_search, query) DESC, similarity_score DESC, i.key
LIMIT #{params.page.limit} OFFSET #{params.page.offset}

mapper.sql:
FROM institution i




  INNER JOIN institution_machine_tag imt on imt.institution_key = i.key
  INNER JOIN machine_tag mt on mt.key = imt.machine_tag_key


  INNER JOIN institution_identifier ii on ii.institution_key = i.key
  INNER JOIN identifier id on id.key = ii.identifier_key


  LEFT JOIN address addr ON addr.key = i.address_key
  LEFT JOIN address mail_addr ON mail_addr.key = i.mailing_address_key




      i.deleted IS NOT NULL


      i.deleted IS NULL



    AND lower(i.code) = lower(#{params.code,jdbcType=VARCHAR})


    AND i.alternative_codes ?? #{params.alternativeCode,jdbcType=VARCHAR}


    AND i.name = #{params.name,jdbcType=VARCHAR}


    AND mt.namespace = #{params.machineTagNamespace,jdbcType=VARCHAR}


    AND mt.name = #{params.machineTagName,jdbcType=VARCHAR}


    AND mt.value = #{params.machineTagValue,jdbcType=VARCHAR}


    AND id.type = #{params.identifierType,jdbcType=OTHER}


    AND id.identifier = #{params.identifier,jdbcType=VARCHAR}


    AND (addr.country IN

        #{item,jdbcType=VARCHAR}

      OR mail_addr.country IN

        #{item,jdbcType=VARCHAR}

    )


    AND (addr.country IN

      #{item,jdbcType=VARCHAR}

    OR mail_addr.country IN

      #{item,jdbcType=VARCHAR}

    )


    AND (normalize_name(addr.city) = normalize_name(#{params.city,jdbcType=VARCHAR})
    OR
    normalize_name(mail_addr.city) = normalize_name(#{params.city,jdbcType=VARCHAR}))


    AND similar_name(i.name, #{params.fuzzyName,jdbcType=VARCHAR})


    AND i.type @> ARRAY[#{params.type,jdbcType=OTHER}]::enum_institution_type[]


    AND i.institutional_governance @> ARRAY[#{params.institutionalGovernance,jdbcType=OTHER}]::enum_institution_governance[]


    AND i.disciplines && ARRAY

      #{item,jdbcType=VARCHAR}::text



    AND i.active = #{params.active,jdbcType=OTHER}


    AND i.master_source = #{params.masterSourceType,jdbcType=OTHER}




        AND i.number_specimens = #{params.numberSpecimens.exactValue,jdbcType=INTEGER}



          AND i.number_specimens >= #{params.numberSpecimens.lowerBound,jdbcType=INTEGER}


          AND i.number_specimens <= #{params.numberSpecimens.higherBound,jdbcType=INTEGER}







        AND i.occurrence_count = #{params.occurrenceCount.exactValue,jdbcType=INTEGER}



          AND i.occurrence_count >= #{params.occurrenceCount.lowerBound,jdbcType=INTEGER}


          AND i.occurrence_count <= #{params.occurrenceCount.higherBound,jdbcType=INTEGER}







        AND i.type_specimen_count = #{params.typeSpecimenCount.exactValue,jdbcType=INTEGER}



          AND i.type_specimen_count >= #{params.typeSpecimenCount.lowerBound,jdbcType=INTEGER}


          AND i.type_specimen_count <= #{params.typeSpecimenCount.higherBound,jdbcType=INTEGER}





    AND i.display_on_NHCPortal = #{params.displayOnNHCPortal,jdbcType=BOOLEAN}


    AND i.replaced_by = #{params.replacedBy,jdbcType=OTHER}


    AND i.key IN

      #{item,jdbcType=VARCHAR}

mapper.select:
SELECT 
FROM institution i
WHERE i.deleted IS NOT NULL

  AND i.replaced_by = #{replacedBy,jdbcType=OTHER}

ORDER BY i.created DESC, i.key

  LIMIT #{page.limit} OFFSET #{page.offset}

mapper.update:
UPDATE institution SET occurrence_count = aux.occ_count, type_specimen_count = aux.ts_count FROM ( VALUES
(#{item.key,jdbcType=OTHER}, #{item.occurrenceCount,jdbcType=INTEGER}, #{item.typeSpecimenCount,jdbcType=INTEGER})
) AS aux (entity_key, occ_count, ts_count) WHERE key = aux.entity_key::uuid

mapper.delete:
DELETE FROM machine_tag USING institution_machine_tag
WHERE
machine_tag_key = machine_tag.key AND
institution_key = #{targetEntityKey,jdbcType=OTHER} AND
namespace = #{namespace}

  AND name = #{name}

mapper.select:
SELECT DISTINCT ON (i.key) 
FROM institution i
INNER JOIN institution_machine_tag im ON im.institution_key = i.key
INNER JOIN machine_tag m ON im.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}


ORDER BY i.key, i.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT i.key)
FROM institution i
INNER JOIN institution_machine_tag im ON im.institution_key = i.key
INNER JOIN machine_tag m ON im.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}

mapper.update:
UPDATE occurrence_mapping
SET
WHERE
occurrence_mapping_key = occurrence_mapping.key
AND institution_key = #{targetEntityKey,jdbcType=OTHER}
AND occurrence_mapping_key = #{occurrenceMappingKey,jdbcType=INTEGER}

DELETE FROM occurrence_mapping USING institution_occurrence_mapping
WHERE
occurrence_mapping_key = occurrence_mapping.key
AND institution_key = #{targetEntityKey,jdbcType=OTHER}
AND occurrence_mapping_key = #{occurrenceMappingKey,jdbcType=INTEGER}

mapper.insert:
UPDATE institution
SET master_sync_metadata_key = #{metadataKey,jdbcType=INTEGER},
    master_source = #{masterSourceType,jdbcType=OTHER}
WHERE key = #{targetEntityKey,jdbcType=OTHER}

mapper.select:
