mapper.select:
SELECT 
FROM network n
WHERE n.key = #{key,jdbcType=OTHER}

SELECT title
FROM network
WHERE key = #{key,jdbcType=OTHER}

SELECT FROM network n, to_tsquery('english',regexp_replace(quote_literal(unaccent(trim(#{query}))),'\s+',':*&','g')||':*') AS query WHERE deleted IS NULL
ORDER BY ts_rank_cd(n.fulltext_search, query) DESC, n.created DESC, key
LIMIT #{page.limit} OFFSET #{page.offset}

mapper.update:
UPDATE network_contact
SET is_primary=false
WHERE network_key=#{targetEntityKey,jdbcType=OTHER} AND type=#{type,jdbcType=OTHER}

UPDATE network_contact
SET is_primary=#{primary,jdbcType=BOOLEAN},
    type=#{type,jdbcType=OTHER}
WHERE network_key=#{targetEntityKey,jdbcType=OTHER} AND contact_key=#{contactKey,jdbcType=INTEGER}

mapper.select:
SELECT true
FROM network_contact
WHERE network_key = #{targetEntityKey,jdbcType=OTHER}
  AND contact_key = #{contactKey,jdbcType=INTEGER}
LIMIT 1

mapper.delete:
DELETE FROM machine_tag USING network_machine_tag
WHERE
machine_tag_key = machine_tag.key AND
network_key = #{targetEntityKey,jdbcType=OTHER} AND
namespace = #{namespace}

  AND name = #{name}

mapper.select:
SELECT DISTINCT ON (n.key) 
FROM network n
INNER JOIN network_machine_tag nm ON nm.network_key = n.key
INNER JOIN machine_tag m ON nm.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}


ORDER BY n.key, n.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT n.key)
FROM network n
INNER JOIN network_machine_tag nm ON nm.network_key = n.key
INNER JOIN machine_tag m ON nm.machine_tag_key = m.key
WHERE m.namespace = #{namespace,jdbcType=VARCHAR}

  AND m.name=#{name,jdbcType=VARCHAR}

    AND m.value=#{value,jdbcType=VARCHAR}

SELECT DISTINCT ON (n.key) 
FROM network n
INNER JOIN network_identifier ON network_key = n.key
INNER JOIN identifier ide ON identifier_key = ide.key
WHERE identifier = #{identifier,jdbcType=VARCHAR}

  AND ide.type=#{type,jdbcType=OTHER}

ORDER BY n.key, n.created DESC

  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(DISTINCT n.key)
FROM network n
INNER JOIN network_identifier ON network_key = n.key
INNER JOIN identifier ide ON identifier_key = ide.key
WHERE identifier = #{identifier,jdbcType=VARCHAR}

  AND ide.type=#{type,jdbcType=OTHER}
