mapper.select:
SELECT 
FROM collection c
WHERE c.key = #{key,jdbcType=OTHER}

SELECT 
FROM collection c
WHERE c.institution_key = #{institutionKey,jdbcType=OTHER}
AND c.deleted IS NULL
ORDER BY c.created DESC, c.key

  LIMIT #{page.limit} OFFSET #{page.offset}

mapper.delete:
DELETE FROM collection_contact
WHERE
collection_key = #{entityKey,jdbcType=OTHER} AND collection_staff_key = #{staffKey,jdbcType=INTEGER}
